import 'package:easy_localization/easy_localization.dart';import 'package:flutter/material.dart';import 'package:nodelabs/core/theme/app_theme.dart';/// MovieErrorWidget ekranı@immutablefinal class MovieErrorWidget extends StatelessWidget {  /// MovieErrorWidget yapıcı methot  const MovieErrorWidget({    required this.message,    required this.onRetry,    super.key,  });  /// Hata mesajını içeren metin  final String message;  /// yeniden yüklemek için event  final VoidCallback? onRetry;  @override  Widget build(BuildContext context) {    return Center(      child: Padding(        padding: const EdgeInsets.all(24),        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: [            const Icon(              Icons.error_outline,              size: 64,              color: AppTheme.textSecondary,            ),            const SizedBox(height: 16),            Text(              'home.errorLoadingMovies'.tr(),              style: const TextStyle(                fontSize: 18,                fontWeight: FontWeight.bold,                color: AppTheme.textPrimary,              ),              textAlign: TextAlign.center,            ),            const SizedBox(height: 8),            Text(              message,              style: const TextStyle(                fontSize: 14,                color: AppTheme.textSecondary,              ),              textAlign: TextAlign.center,            ),            const SizedBox(height: 24),            ElevatedButton(              onPressed: onRetry,              style: ElevatedButton.styleFrom(                backgroundColor: AppTheme.primaryRed,                foregroundColor: Colors.white,              ),              child: Text('app.retry'.tr()),            ),          ],        ),      ),    );  }}